(1114, '_site_transient_update_plugins', 'O:8:"stdClass":4:{s:12:"last_checked";i:1498812561;s:8:"response";a:5:{s:43:"custom-post-type-ui/custom-post-type-ui.php";O:8:"stdClass":8:{s:2:"id";s:33:"w.org/plugins/custom-post-type-ui";s:4:"slug";s:19:"custom-post-type-ui";s:6:"plugin";s:43:"custom-post-type-ui/custom-post-type-ui.php";s:11:"new_version";s:5:"1.5.4";s:3:"url";s:50:"https://wordpress.org/plugins/custom-post-type-ui/";s:7:"package";s:68:"https://downloads.wordpress.org/plugin/custom-post-type-ui.1.5.4.zip";s:6:"tested";s:5:"4.8.0";s:13:"compatibility";O:8:"stdClass":0:{}}s:39:"siteorigin-panels/siteorigin-panels.php";O:8:"stdClass":8:{s:2:"id";s:31:"w.org/plugins/siteorigin-panels";s:4:"slug";s:17:"siteorigin-panels";s:6:"plugin";s:39:"siteorigin-panels/siteorigin-panels.php";s:11:"new_version";s:5:"2.5.7";s:3:"url";s:48:"https://wordpress.org/plugins/siteorigin-panels/";s:7:"package";s:66:"https://downloads.wordpress.org/plugin/siteorigin-panels.2.5.7.zip";s:6:"tested";s:3:"4.8";s:13:"compatibility";O:8:"stdClass":0:{}}s:25:"seed-fonts/seed-fonts.php";O:8:"stdClass":9:{s:2:"id";s:24:"w.org/plugins/seed-fonts";s:4:"slug";s:10:"seed-fonts";s:6:"plugin";s:25:"seed-fonts/seed-fonts.php";s:11:"new_version";s:5:"1.1.3";s:3:"url";s:41:"https://wordpress.org/plugins/seed-fonts/";s:7:"package";s:59:"https://downloads.wordpress.org/plugin/seed-fonts.1.1.3.zip";s:14:"upgrade_notice";s:213:"<ul>\n<li>Add Moonjelly and CS Chatthai UI fonts.</li>\n<li>Remove old font files (.eot and .ttf). Now using only .woff and .woff2.</li>\n<li>Now we can upload fonts to /wp-content/uploads/fonts/FONT-NAME.</li>\n</ul>";s:6:"tested";s:3:"4.8";s:13:"compatibility";O:8:"stdClass":0:{}}s:39:"so-widgets-bundle/so-widgets-bundle.php";O:8:"stdClass":8:{s:2:"id";s:31:"w.org/plugins/so-widgets-bundle";s:4:"slug";s:17:"so-widgets-bundle";s:6:"plugin";s:39:"so-widgets-bundle/so-widgets-bundle.php";s:11:"new_version";s:5:"1.9.2";s:3:"url";s:48:"https://wordpress.org/plugins/so-widgets-bundle